mapper.select:
select a.c_number, a.c_title, a.c_content, a.c_pass, a.m_id, a.c_date, a.c_re_status, a.c_ref from (
select client.c_number, client.c_title, client.c_content, client.c_pass, client.m_id, client.c_date, client.c_re_status, client.c_ref from f_consulting admin, f_consulting client where admin.c_ref=client.c_ref
) a where a.m_id='admin' or a.m_id=#{m_id}
